import React, { Component } from 'react';
import Timeset from './timeset';
/* Timeset props
* breakGap = breakT
* startTime = sessionT
*/

class Clock extends Component {
  constructor(props) {
    super(props);
    this.state ={
      breakT: 0,
      sessionT: 9000
    }
    //this.
  }

  toHHMMSS(time) {
      const myNum = parseInt(time, 10);
      let hours   = Math.floor(myNum / 3600);
      let minutes = Math.floor((myNum - (hours * 3600)) / 60);
      let seconds = myNum - (hours * 3600) - (minutes * 60);

      if (hours   < 10) {hours   = "0"+hours;}
      if (minutes < 10) {minutes = "0"+minutes;}
      if (seconds < 10) {seconds = "0"+seconds;}
      return hours+':'+minutes+':'+seconds;
  }

  render() {
    return(
      <div>
        <Timeset value={Timeset.state.breakGap}/>
        <div>{this.toHHMMSS(this.state.sessionT)}</div>
      </div>
    )
  }
}


export default Clock;
